CREATE TABLE TRABAJADOR(SEGURO INTEGER PRIMARY KEY, NOMBRE VARCHAR2(25),EDAD INTEGER);CREATE TABLE NOMINA (ID_NOMINA INTEGER, SEGURO INTEGER,SUELDO_BASE FLOAT,HORAS_LABORADAS INTEGER, FECHA_PAGO DATE,CONSTRAINT PK_ID_NOMINA PRIMARY KEY (ID_NOMINA),CONSTRAINT FK1_SEGURO FOREIGN KEY (SEGURO) REFERENCESTRABAJADOR (SEGURO));CREATE SEQUENCE SEC_NOMINASTART WITH 1INCREMENT BY 1nomaxvalue;CREATE OR REPLACE PROCEDURE GUARDAR_NOMINA(MY_ID_NOMINA OUT INTEGER,MY_SEGURO IN INTEGER , MY_SUELDO_BASE IN INTEGER) ASBEGINSELECT SEC_NOMINA.NEXTVAL INTO MY_ID_NOMINA FROM DUAL;INSERT INTO NOMINA(ID_NOMINA, SEGURO, SUELDO_BASE)VALUES (MY_ID_NOMINA,MY_SEGURO,MY_SUELDO_BASE);END;/CREATE OR REPLACE PROCEDURE GENERAR_TRABAJADOR (MY_SEGURO ININTEGER, MY_NOMBREIN VARCHAR2, MY_EDAD IN INTEGER,MY_ID_NOMINA OUT INTEGER, MY_SUELDO_BASE IN FLOAT) ASBEGININSERT INTO TRABAJADOR VALUES (MY_SEGURO, MY_NOMBRE,MY_EDAD);GUARDAR_NOMINA (MY_ID_NOMINA, MY_SEGURO,MY_SUELDO_BASE);END;/DECLAREBALOR INTEGER;BEGINGENERAR_TRABAJADOR(1,'ANA',28,VALOR, 6000);GENERAR_TRABAJADOR(2,'LUIS',26,VALOR,8500);END;/
